import type {Vocabulary, KeywordDefinition, ErrorNoParams} from "ajv"
import type {DefinitionOptions, GetDefinition} from "ajv-keywords/src/definitions/_types"
import typeofDef from "ajv-keywords/src/definitions/typeof"
import instanceofDef from "ajv-keywords/src/definitions/instanceof"
import range from "ajv-keywords/src/definitions/range"
import exclusiveRange from "ajv-keywords/src/definitions/exclusiveRange"
import regexp from "ajv-keywords/src/definitions/regexp"
import transform from "ajv-keywords/src/definitions/transform"
import uniqueItemProperties from "ajv-keywords/src/definitions/uniqueItemProperties"
import allRequired from "ajv-keywords/src/definitions/allRequired"
import anyRequired from "ajv-keywords/src/definitions/anyRequired"
import oneRequired from "ajv-keywords/src/definitions/oneRequired"
import patternRequired, {PatternRequiredError} from "ajv-keywords/src/definitions/patternRequired"
import prohibited from "ajv-keywords/src/definitions/prohibited"
import deepProperties from "ajv-keywords/src/definitions/deepProperties"
import deepRequired from "ajv-keywords/src/definitions/deepRequired"
import dynamicDefaults from "ajv-keywords/src/definitions/dynamicDefaults"
import selectDef, {SelectError} from "ajv-keywords/src/definitions/select"

const definitions: GetDefinition<KeywordDefinition>[] = [
  typeofDef,
  instanceofDef,
  range,
  exclusiveRange,
  regexp,
  transform,
  uniqueItemProperties,
  allRequired,
  anyRequired,
  oneRequired,
  patternRequired,
  prohibited,
  deepProperties,
  deepRequired,
  dynamicDefaults,
]

export default function ajvKeywords(opts?: DefinitionOptions): Vocabulary {
  return definitions.map((d) => d(opts)).concat(selectDef(opts))
}

export type AjvKeywordsError =
  | PatternRequiredError
  | SelectError
  | ErrorNoParams<
      | "range"
      | "exclusiveRange"
      | "anyRequired"
      | "oneRequired"
      | "allRequired"
      | "deepProperties"
      | "deepRequired"
      | "dynamicDefaults"
      | "instanceof"
      | "prohibited"
      | "regexp"
      | "transform"
      | "uniqueItemProperties"
    >

module.exports = ajvKeywords
