import type {CodeKeywordDefinition, SchemaObject} from "ajv/lib/types"
import type {KeywordCxt} from "ajv/lib/compile/validate"
import {alwaysValidSchema} from "ajv/lib/compile/util"
import {validateArray} from "ajv/lib/vocabularies/code"
import {_, not} from "ajv/lib/compile/codegen"
import {checkMetadata} from "ajv/lib/vocabularies/jtd/metadata"
import {checkNullable} from "ajv/lib/vocabularies/jtd/nullable"
import {typeError, _JTDTypeError} from "ajv/lib/vocabularies/jtd/error"

export type JTDElementsError = _JTDTypeError<"elements", "array", SchemaObject>

const def: CodeKeywordDefinition = {
  keyword: "elements",
  schemaType: "object",
  error: typeError("array"),
  code(cxt: KeywordCxt) {
    checkMetadata(cxt)
    const {gen, data, schema, it} = cxt
    if (alwaysValidSchema(it, schema)) return
    const [valid] = checkNullable(cxt)
    gen.if(not(valid), () =>
      gen.if(
        _`Array.isArray(${data})`,
        () => gen.assign(valid, validateArray(cxt)),
        () => cxt.error()
      )
    )
    cxt.ok(valid)
  },
}

export default def
