import type {Vocabulary} from "ajv/lib/types"
import refKeyword from "ajv/lib/vocabularies/jtd/ref"
import typeKeyword, {JTDTypeError} from "ajv/lib/vocabularies/jtd/type"
import enumKeyword, {JTDEnumError} from "ajv/lib/vocabularies/jtd/enum"
import elements, {JTDElementsError} from "ajv/lib/vocabularies/jtd/elements"
import properties, {JTDPropertiesError} from "ajv/lib/vocabularies/jtd/properties"
import optionalProperties from "ajv/lib/vocabularies/jtd/optionalProperties"
import discriminator, {JTDDiscriminatorError} from "ajv/lib/vocabularies/jtd/discriminator"
import values, {JTDValuesError} from "ajv/lib/vocabularies/jtd/values"
import union from "ajv/lib/vocabularies/jtd/union"
import metadata from "ajv/lib/vocabularies/jtd/metadata"

const jtdVocabulary: Vocabulary = [
  "definitions",
  refKeyword,
  typeKeyword,
  enumKeyword,
  elements,
  properties,
  optionalProperties,
  discriminator,
  values,
  union,
  metadata,
  {keyword: "additionalProperties", schemaType: "boolean"},
  {keyword: "nullable", schemaType: "boolean"},
]

export default jtdVocabulary

export type JTDErrorObject =
  | JTDTypeError
  | JTDEnumError
  | JTDElementsError
  | JTDPropertiesError
  | JTDDiscriminatorError
  | JTDValuesError
