"use strict";

var absolutize = require("relateurl/lib/relate/absolutize");
var relativize = require("relateurl/lib/relate/relativize");



function relateUrl(siteUrlObj, urlObj, options)
{
	absolutize(urlObj, siteUrlObj, options);
	relativize(urlObj, siteUrlObj, options);
	
	return urlObj;
}



module.exports = relateUrl;
