import "terser/lib/transform.js";
import "terser/lib/mozilla-ast.js";
import { minify } from "terser/lib/minify.js";

export { minify, minify_sync } from "terser/lib/minify.js";
export { run_cli as _run_cli } from "terser/lib/cli.js";

export async function _default_options() {
    const defs = {};

    Object.keys(infer_options({ 0: 0 })).forEach((component) => {
        const options = infer_options({
            [component]: {0: 0}
        });

        if (options) defs[component] = options;
    });
    return defs;
}

async function infer_options(options) {
    try {
        await minify("", options);
    } catch (error) {
        return error.defs;
    }
}
